%snappydata
{
    import org.apache.zeppelin.interpreter.QueryBuilder._
    val dataSource = z.get("dataSource").asInstanceOf[String]
    val fileFormat = z.get("fileFormat").asInstanceOf[String]
    println("List of already deployed packages (on fresh installations this list will be empty.)")
    val ss = new org.apache.spark.sql.SnappySession(sc)
    ss.sql("list packages").show(false)

    printHTML(getDependencyJarNames(dataSource), "To be deployed from Maven Repository")
    printHTML(getDependencyJarNames(fileFormat), "To be deployed from Spark-packages")
    val depsParams = renderDepsUI(dataSource,fileFormat,z)
    val incompleteDeps = depsParams.filter(_._2.productIterator.map(_.asInstanceOf[String]).contains(""))
    if(incompleteDeps.keys.size != 0){
        printHTML(incompleteDeps.keys.toList.map(x => s"""<font color=blue><i>$x</i></font>"""),s"""Warning : Dependency jar parameter(s) left blank. Ignore if package is already installed on the system.""")
        val t = incompleteDeps.keys.toList
    }else{ 
       depsParams.foreach((t) => ss.sql(s"""deploy package ${t._2._1} '${t._2._2}' path '${t._2._3}'"""))
       z.run("20191009-094747_127437526")
    }
}

%snappydata
{
    import org.apache.zeppelin.interpreter.QueryBuilder._

    val datasetName = z.input("Dataset name").asInstanceOf[String]
    if(datasetName.length == 0 ){
        printHTML(List("Please provide name of the dataset to create the extenal table."),"Warning")
    }else{
       createExternalTable(sc,z,datasetName)
       //Clean all variables
       z.remove("dataSource")
       z.remove("fileFormat")
       z.remove("dataSourceParams")
       z.remove("path")
       z.remove("fileParams")
    }
}
