%snappydata
{  
var flag:Boolean=false
var key:String=" "
var value:String=" "
var map1:Map[String,String] = Map()

val datasetName =z.get("datasetName").asInstanceOf[String]
val SqlServerTable=z.get("SqlServerTable").asInstanceOf[String]
val user=z.get("user").asInstanceOf[String]
val password=z.get("password").asInstanceOf[String]
val url=z.get("url").asInstanceOf[String]
val database=z.get("database").asInstanceOf[String]

val ss = new org.apache.spark.sql.SnappySession(snc.sparkContext)
val parameterString = z.input("Enter optional parameters in the form of <key>=<value> separated by comma (,)", "").asInstanceOf[String]

if(parameterString!=""){
for (returnValue <- parameterString.split(","))
{
  for(ret <- returnValue.split("="))
    {
      if(flag==false)
      {
        key=ret

        flag=true
      }
      else
      {
        value=ret
        flag=false
       }
    }
 map1 += key -> value
 }
    var stringToConcat = ""
    map1.keys.foreach{ j =>
    var temp=j
    var temp2=map1(j)
    stringToConcat += ","+temp+" "+"'"+temp2+"'"
    }

ss.sql(s"drop table if exists $datasetName")
val connectionString=z.get("connectionString").asInstanceOf[String]
val connectionString2=connectionString.concat(stringToConcat)
val finalConnectionString=connectionString2.concat(")")
ss.sql(finalConnectionString)
}
else{
 println("No extra parameters added.")
 ss.sql(s"drop table if exists $datasetName")
 ss.sql(s"create external table $datasetName using JDBC options (url '$url',databaseName '$database' , dbtable '$SqlServerTable', user '$user', password '$password', driver 'com.microsoft.sqlserver.jdbc.SQLServerDriver')")
}
println
println(s"External table named $datasetName registered.")
println(s"====== Schema for $datasetName ======")
ss.table(datasetName).printSchema

}
